package p_250207;

public class Oper09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 확장 대입 연산자(복합 대입 연산자)
		// +=, -=, *=, /=, %=
		
		int a = 10, b = 3;
		
		System.out.println(a+=b);		// a = a + b	13
		System.out.println(a-=b);		// a = a - b	10
		System.out.println(a*=b);		// a = a * b	30
		System.out.println(a/=b);		// a = a / b	10
		System.out.println(a%=b);		// a = a % b	1
		
	}

}
